// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interface/IStrategy.sol";

/**
 * @title BaseStrategy
 * @dev Abstract contract providing foundational logic for individual strategy implementations.
 * Strategy implementations will inherit from this contract and override virtual functions.
 * This contract itself is NOT meant to be deployed directly.
 */
abstract contract BaseStrategy is IStrategy {
    // --- State Variables ---

    address public immutable hypeliquidRouter; // Address of the Hypeliquid DEX router
    address public admin; // Admin address for managing strategy parameters (distinct from proxy owner)
    uint8 public leverage; // Leverage factor (e.g., 3 for 3x)
    uint256 public platformFeeBps; // Platform fee in basis points (e.g., 100 for 1%)

    // --- Events ---

    event AdminTransferred(address indexed previousAdmin, address indexed newAdmin);
    event LeverageUpdated(uint8 newLeverage);
    event PlatformFeeUpdated(uint256 newPlatformFeeBps);

    // --- Modifiers ---

    modifier onlyAdmin() {
        require(msg.sender == admin, "BaseStrategy: Caller is not the admin");
        _;
    }

    // --- Constructor ---

    /**
     * @param _hypeliquidRouter Address of the Hypeliquid DEX router/entry point.
     * @param _platformFeeBps Initial platform fee in basis points.
     * @param _initialLeverage The leverage factor for the strategy.
     */
    constructor(
        address _hypeliquidRouter,
        uint256 _platformFeeBps,
        uint8 _initialLeverage
    ) {
        require(_hypeliquidRouter != address(0), "BaseStrategy: Router cannot be zero address");
        require(_platformFeeBps <= 1000, "BaseStrategy: Fee too high"); // Max 10%
        require(_initialLeverage > 0 && _initialLeverage <= 10, "BaseStrategy: Invalid leverage");

        admin = msg.sender; // Deployer is the initial admin
        hypeliquidRouter = _hypeliquidRouter;
        platformFeeBps = _platformFeeBps;
        leverage = _initialLeverage;

        emit AdminTransferred(address(0), msg.sender);
        emit PlatformFeeUpdated(_platformFeeBps);
        emit LeverageUpdated(_initialLeverage);
    }

    // --- Admin Functions ---

    /**
     * @dev Updates the leverage factor for the strategy.
     * @param _newLeverage The new leverage value.
     */
    function updateLeverage(uint8 _newLeverage) external virtual onlyAdmin {
        require(_newLeverage > 0 && _newLeverage <= 10, "BaseStrategy: Invalid leverage");
        leverage = _newLeverage;
        emit LeverageUpdated(_newLeverage);
    }

    /**
     * @dev Updates the platform fee.
     * @param _newPlatformFeeBps The new fee in basis points.
     */
    function updatePlatformFee(uint256 _newPlatformFeeBps) external virtual onlyAdmin {
        require(_newPlatformFeeBps <= 1000, "BaseStrategy: Fee too high"); // Max 10%
        platformFeeBps = _newPlatformFeeBps;
        emit PlatformFeeUpdated(_newPlatformFeeBps);
    }

    /**
     * @dev Transfers admin role to a new address.
     * @param _newAdmin The address of the new admin.
     */
    function transferAdmin(address _newAdmin) external virtual onlyAdmin {
        require(_newAdmin != address(0), "BaseStrategy: New admin is the zero address");
        address oldAdmin = admin;
        admin = _newAdmin;
        emit AdminTransferred(oldAdmin, _newAdmin);
    }

    // --- IStrategy Interface Implementation (Virtual Functions) ---
    // Derived contracts MUST override these functions with specific logic.

    /**
     * @dev Increases the user's position. Override in derived contracts.
     * Implementation should interact with Hypeliquid, calculate fees, update state.
     */
    function addLiquidity(uint256 /* amount */, address /* feeRecipient */) external virtual override {
        revert("BaseStrategy: addLiquidity must be implemented in derived contract");
    }

    /**
     * @dev Decreases the user's position. Override in derived contracts.
     * Implementation should interact with Hypeliquid, calculate fees, update state.
     */
    function removeLiquidity(uint256 /* amount */, address /* feeRecipient */) external virtual override {
         revert("BaseStrategy: removeLiquidity must be implemented in derived contract");
    }

    /**
     * @dev Executes periodic strategy logic (e.g., rebalancing). Override in derived contracts.
     */
    function execute() external virtual override {
         revert("BaseStrategy: execute must be implemented in derived contract");
    }

    /**
     * @dev Calculates profit or loss. Override in derived contracts.
     * Needs access to position entry data and current market prices.
     */
    function getProfitLoss() external view virtual override returns (int256 pnl) {
        // Base implementation returns 0, derived contracts must calculate actual PnL.
        return 0;
    }

    /**
     * @dev Returns trade history. Override in derived contracts.
     * Needs to retrieve stored trade records.
     */
    function getTradeHistory() external view virtual override returns (Trade[] memory history) {
        // Base implementation returns empty array, derived contracts must manage history.
        return history; // Returns empty array
    }

    /**
     * @dev Returns the strategy's view of the collateral balance. Override in derived contracts.
     * This might differ from the proxy's balance depending on implementation.
     */
    function getBalance() external view virtual override returns (uint256 balance) {
        // Base implementation returns 0, derived contracts must track balance.
        return 0;
    }

    /**
     * @dev Returns current asset holdings. Override in derived contracts.
     * Needs to track assets held by the strategy for the user.
     */
    function getHoldings() external view virtual override returns (Holding[] memory holdings) {
        // Base implementation returns empty array, derived contracts must track holdings.
        return holdings; // Returns empty array
    }

    /**
     * @dev Returns the owner of the proxy controlling this strategy instance.
     * IMPORTANT: Implementations need a mechanism to know the proxy owner,
     * as `msg.sender` inside delegatecall is the proxy itself.
     * This might involve storing the owner during initialization triggered by the proxy,
     * or other context-aware patterns.
     */
    function getOwner() external view virtual override returns (address ownerAddress) {
        // Base implementation returns zero address. Derived contracts MUST implement this correctly.
        return address(0);
    }

    // --- Internal Helper Functions (Optional) ---

    /**
     * @dev Internal function to calculate platform fee amount.
     * @param _value The value on which the fee is calculated (e.g., trade size).
     * @return feeAmount The calculated fee amount.
     */
    function _calculateFee(uint256 _value) internal view virtual returns (uint256 feeAmount) {
        if (platformFeeBps == 0) {
            return 0;
        }
        // Calculate fee using basis points (BPS), 1 BPS = 0.01%
        // 10000 BPS = 100%
        feeAmount = (_value * platformFeeBps) / 10000;
    }

    // TODO: Add internal functions for Hypeliquid interactions if common logic exists.
    // function _swap(...) internal virtual { ... }
    // function _getAssetPrice(...) internal view virtual returns (uint256) { ... }

}

