// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./BaseStrategy.sol";
import "./interface/IStrategy.sol";
// TODO: Replace with actual IERC20 interface
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// TODO: Import necessary Hypeliquid interfaces for margin/shorting

/**
 * @title DumpStrategy
 * @dev Implements a 3x leveraged SHORT strategy on meme assets (DOGE, SHIB, PEPE, BONK, WIF).
 * Inherits from BaseStrategy and implements the IStrategy interface.
 * Meant to be used as the implementation contract for StrategyProxy via delegatecall.
 */
contract DumpStrategy is BaseStrategy {
    // --- Constants ---
    // Using the same asset addresses as ZoomStrategy for consistency
    // TODO: Replace with actual mainnet/testnet addresses for these meme coins on the target EVM chain
    address public constant DOGE = 0x1121AcC14c63f3C872BFcA497d10926A6098AAc5;
    address public constant SHIB = 0x95aD61b0a150d79219dCF64E1E6Cc01f0B64C4cE;
    address public constant PEPE = 0x25d887Ce7a35172C62FeBFD67a1856F20FaEbB00;
    address public constant BONK = 0x09199d9A5F4448D0848e4395D065e1ad9c4a1F74;
    address public constant WIF  = 0x886c869cDc619214138C87f1DB0ADa522b16Dfa3;
    address public constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48; // Example: Mainnet USDC

    address[] public strategyAssets; // Assets managed by this strategy

    // Portfolio index mappings
    mapping(address => uint16) private assetToIndex;

    // --- State Variables (Managed Per Proxy via DelegateCall) ---

    address public proxyOwner; // Owner of the proxy contract

    // User's position data (keyed by proxy owner)
    struct PositionData {
        uint256 collateralDeposited;
        uint256 entryValueUsd; // Value of assets shorted
        mapping(address => uint256) shortedAssetAmounts; // Amount of each meme coin shorted
        bool initialized;
    }
    PositionData internal position;

    Trade[] internal tradeHistory;

    // --- Events ---
    event Initialized(address indexed owner);
    event PositionUpdated(address indexed owner, string action, uint256 amount);
    event Rebalanced(address indexed owner);
    event TradeRecorded(address indexed owner, Trade trade);

    // --- Constructor ---
    /**
     * @dev Sets up the base strategy parameters.
     * @param _hypeliquidRouter Address of the Hypeliquid DEX router.
     * @param _platformFeeBps Platform fee in basis points.
     */
    constructor(address _hypeliquidRouter, uint256 _platformFeeBps)
        BaseStrategy(_hypeliquidRouter, _platformFeeBps, 3) // DumpStrategy uses 3x leverage
    {
        strategyAssets = [DOGE, SHIB, PEPE, BONK, WIF];
        
        // Initialize index mappings (these should match Hyperliquid's indices)
        assetToIndex[DOGE] = 0;
        assetToIndex[SHIB] = 1;
        assetToIndex[PEPE] = 2;
        assetToIndex[BONK] = 3;
        assetToIndex[WIF] = 4;
    }

    // --- Initialization ---
    /**
     * @dev Initializes the strategy implementation for a specific proxy owner.
     * @param _owner The address of the StrategyProxy owner.
     */
    function initialize(address _owner) external {
        // Remove redundant check
        // require(proxyOwner == address(0), "Already initialized");
        require(!position.initialized, "DumpStrategy: Already initialized");
        require(_owner != address(0), "DumpStrategy: Owner cannot be zero");

        proxyOwner = _owner;
        position.initialized = true;
        emit Initialized(_owner);
    }

    // --- IStrategy Interface Implementation ---

    /**
     * @dev Increases the user's SHORT position by adding collateral and short-selling meme assets.
     */
    function addLiquidity(uint256 amount, address feeRecipient) external override {
        // Remove direct call check that's incompatible with delegatecall
        // require(msg.sender == address(this), "DumpStrategy: Direct call not allowed");
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");

        uint256 fee = _calculateFee(amount);
        uint256 amountAfterFee = amount - fee;

        position.collateralDeposited += amountAfterFee;

        // Calculate how much to short of each meme coin
        uint256 numAssets = strategyAssets.length;
        uint256[] memory allocations = new uint256[](numAssets);
        
        // DOGE and SHIB get 30% each, others split remaining 40%
        allocations[0] = 30; // DOGE
        allocations[1] = 30; // SHIB
        allocations[2] = 14; // PEPE
        allocations[3] = 13; // BONK
        allocations[4] = 13; // WIF

        for(uint i = 0; i < numAssets; i++) {
            address asset = strategyAssets[i];
            uint256 valueForAsset = (amountAfterFee * uint256(leverage) * allocations[i]) / 100;
            uint256 assetPrice = IERC20(hypeliquidRouter).balanceOf(asset); // Mock price feed
            uint256 amountToShort = (valueForAsset * 1e18) / assetPrice;
            
            // Execute short on Hyperliquid:
            // 1. Borrow meme coin
            // hypeliquidRouter.borrow(asset, amountToShort);
            // 2. Sell borrowed meme coin for USDC
            // hypeliquidRouter.swap(asset, USDC, amountToShort);
            
            // Track shorted position
            position.shortedAssetAmounts[asset] += amountToShort;
        }

        position.entryValueUsd += amountAfterFee * uint256(leverage);

        // Record trade
        Trade memory newTrade = Trade({
            timestamp: block.timestamp,
            tradeType: "Add Meme Short",
            assetIn: USDC,
            amountIn: amount,
            assetOut: address(0), // Multiple assets
            amountOut: 0,        // Multiple amounts
            feeAmount: fee,
            feeAsset: USDC
        });
        tradeHistory.push(newTrade);
        emit TradeRecorded(proxyOwner, newTrade);

        // Transfer fee
        if (fee > 0) {
            IERC20(USDC).transfer(feeRecipient, fee);
        }

        emit PositionUpdated(proxyOwner, "Add Meme Short", amount);
    }

    /**
     * @dev Decreases the user's SHORT position by buying back meme assets.
     */
    function removeLiquidity(uint256 amount, address feeRecipient) external override {
        // Remove direct call check that's incompatible with delegatecall
        // require(msg.sender == address(this), "DumpStrategy: Direct call not allowed");
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");
        require(amount <= position.collateralDeposited, "DumpStrategy: Insufficient liquidity");

        uint256 fee = _calculateFee(amount);
        uint256 amountBeforeFee = amount;

        // Calculate proportional reduction for each short position maintaining allocation ratios
        uint256 numAssets = strategyAssets.length;
        for(uint i = 0; i < numAssets; i++) {
            address asset = strategyAssets[i];
            uint256 shortedAmount = position.shortedAssetAmounts[asset];
            uint256 amountToClose = (shortedAmount * amount) / position.collateralDeposited;
            
            if (amountToClose > 0) {
                // Execute short cover on Hyperliquid:
                // 1. Buy back shorted meme coin with USDC
                // hypeliquidRouter.swap(USDC, asset, amountToClose);
                // 2. Repay borrowed meme coin
                // hypeliquidRouter.repay(asset, amountToClose);
                
                position.shortedAssetAmounts[asset] -= amountToClose;
            }
        }

        position.collateralDeposited -= amountBeforeFee;
        position.entryValueUsd -= amountBeforeFee * uint256(leverage);

        // Record trade
        Trade memory newTrade = Trade({
            timestamp: block.timestamp,
            tradeType: "Close Meme Short",
            assetIn: address(0), // Multiple assets
            amountIn: 0,        // Multiple amounts
            assetOut: USDC,
            amountOut: amountBeforeFee,
            feeAmount: fee,
            feeAsset: USDC
        });
        tradeHistory.push(newTrade);
        emit TradeRecorded(proxyOwner, newTrade);

        // Transfer fee
        if (fee > 0) {
            IERC20(USDC).transfer(feeRecipient, fee);
        }

        emit PositionUpdated(proxyOwner, "Close Meme Short", amount);
    }

    /**
     * @dev Executes rebalancing logic for the meme coin short positions.
     */
    function execute() external override {
        // Remove direct call check that's incompatible with delegatecall
        // require(msg.sender == address(this), "DumpStrategy: Direct call not allowed");
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");

        // Get total shorted value and current weights
        uint256 totalShortValue;
        uint256[] memory currentValues = new uint256[](strategyAssets.length);
        uint256[] memory targetWeights = new uint256[](strategyAssets.length);
        
        // Set target weights: DOGE/SHIB 30% each, others split 40%
        targetWeights[0] = 30; // DOGE
        targetWeights[1] = 30; // SHIB
        targetWeights[2] = 14; // PEPE
        targetWeights[3] = 13; // BONK
        targetWeights[4] = 13; // WIF
        
        for(uint i = 0; i < strategyAssets.length; i++) {
            address asset = strategyAssets[i];
            uint256 price = IERC20(hypeliquidRouter).balanceOf(asset); // Mock price feed
            currentValues[i] = position.shortedAssetAmounts[asset] * price / 1e18;
            totalShortValue += currentValues[i];
        }

        if (totalShortValue == 0) return;

        // Rebalance if needed
        for(uint i = 0; i < strategyAssets.length; i++) {
            address asset = strategyAssets[i];
            uint256 targetValue = (totalShortValue * targetWeights[i]) / 100;
            uint256 deviation = absDiff(currentValues[i], targetValue);
            uint256 threshold = targetValue * 5 / 100; // 5% threshold
            
            if (deviation > threshold) {
                if (currentValues[i] > targetValue) {
                    // Reduce short position
                    uint256 excessValue = currentValues[i] - targetValue;
                    uint256 price = IERC20(hypeliquidRouter).balanceOf(asset);
                    uint256 amountToClose = (excessValue * 1e18) / price;
                    
                    // Buy back and repay
                    // hypeliquidRouter.swap(USDC, asset, amountToClose);
                    // hypeliquidRouter.repay(asset, amountToClose);
                    position.shortedAssetAmounts[asset] -= amountToClose;
                    
                } else {
                    // Increase short position
                    uint256 shortfall = targetValue - currentValues[i];
                    uint256 price = IERC20(hypeliquidRouter).balanceOf(asset);
                    uint256 amountToShort = (shortfall * 1e18) / price;
                    
                    // Borrow and sell
                    // hypeliquidRouter.borrow(asset, amountToShort);
                    // hypeliquidRouter.swap(asset, USDC, amountToShort);
                    position.shortedAssetAmounts[asset] += amountToShort;
                }
            }
        }

        emit Rebalanced(proxyOwner);
    }

    /**
     * @dev Calculates current Profit and Loss for the short position.
     */
    function getProfitLoss() external view override returns (int256 pnl) {
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");

        // TODO: Implement PnL Calculation for MEME COIN SHORT positions
        // (Similar to DownStrategy but with meme coin assets/prices)
        return 0; // Placeholder
    }

    /**
     * @dev Returns the trade history.
     */
    function getTradeHistory() external view override returns (Trade[] memory history) {
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");
        return tradeHistory;
    }

    /**
     * @dev Returns the strategy's view of the collateral balance.
     */
    function getBalance() external view override returns (uint256 balance) {
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");
        return position.collateralDeposited;
    }

    /**
     * @dev Returns the current shorted asset amounts.
     */
    function getHoldings() external view override returns (Holding[] memory holdings) {
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");

        holdings = new Holding[](strategyAssets.length);
        for (uint i = 0; i < strategyAssets.length; i++) {
            address asset = strategyAssets[i];
            uint256 amountShorted = position.shortedAssetAmounts[asset];
            // TODO: Get current value using price feed
            uint256 valueUsd = 0; // Placeholder
            holdings[i] = Holding(asset, amountShorted, valueUsd);
        }
        return holdings;
    }

    /**
     * @dev Returns the owner address associated with this strategy instance.
     */
    function getOwner() external view override returns (address ownerAddress) {
        return proxyOwner;
    }

    function getCoinType() external pure returns (CoinType) {
        return CoinType.MEME;
    }

    function getPositionDirection() external pure returns (PositionDirection) {
        return PositionDirection.SHORT;
    }

    function getLeverage() external pure returns (uint32) {
        return 5; // 5x leverage for aggressive short strategy
    }

    function getPortfolioIndices() external view returns (uint16[] memory) {
        uint16[] memory indices = new uint16[](strategyAssets.length);
        for (uint i = 0; i < strategyAssets.length; i++) {
            indices[i] = assetToIndex[strategyAssets[i]];
        }
        return indices;
    }

    /**
     * @dev Opens short positions according to the strategy allocation
     */
    function openPositions(
        address holder,
        uint64 usdcAmount
    ) external returns (uint16[] memory indices, bool[] memory isBuy, uint64[] memory sz) {
        // Remove direct call check that's incompatible with delegatecall
        // require(msg.sender == address(this), "DumpStrategy: Direct call not allowed");
        require(proxyOwner != address(0), "DumpStrategy: Not initialized");
        
        uint256 numAssets = strategyAssets.length;
        indices = new uint16[](numAssets);
        isBuy = new bool[](numAssets);
        sz = new uint64[](numAssets);

        // Distribution for meme assets: DOGE/SHIB 30% each, PEPE/BONK/WIF 13.33% each
        for (uint i = 0; i < numAssets; i++) {
            indices[i] = assetToIndex[strategyAssets[i]];
            isBuy[i] = false; // Always sell for short positions
            
            if (i < 2) { // DOGE and SHIB get 30% each
                sz[i] = (usdcAmount * 30) / 100;
            } else { // PEPE, BONK, and WIF split the remaining 40%
                sz[i] = (usdcAmount * 40) / (100 * 3);
            }
        }

        return (indices, isBuy, sz);
    }

    // --- Internal Helper Functions ---
    // TODO: Add helpers for DEX shorting/covering interactions, price fetching, etc. specific to meme coins.

    // Helper function for absolute difference
    function absDiff(uint256 a, uint256 b) internal pure returns (uint256) {
        return a > b ? a - b : b - a;
    }
}

