contracts/interface/IStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// Enums for strategy classification
enum CoinType { MAJOR, MEME }
enum PositionDirection { LONG, SHORT }

// Basic struct to represent a trade record
struct Trade {
    uint256 timestamp;
    string tradeType; // e.g., "Entry", "Exit", "Add", "Reduce", "Rebalance"
    address assetIn; // Address of the asset being spent/sold
    uint256 amountIn;
    address assetOut; // Address of the asset being received/bought
    uint256 amountOut;
    uint256 feeAmount; // Fee paid for this trade
    address feeAsset; // Asset the fee was paid in
}

// Basic struct to represent a current holding
struct Holding {
    address asset; // Address of the asset held
    uint256 amount; // Amount of the asset held
    uint256 valueUsd; // Optional: Current value in USD (or other base currency)
}

interface IStrategy {
    function addLiquidity(uint256 amount, address feeRecipient) external;
    function removeLiquidity(uint256 amount, address feeRecipient) external;
    function execute() external;
    function getProfitLoss() external view returns (int256);
    function getOwner() external view returns (address);
    function getBalance() external view returns (uint256);
    function getHoldings() external view returns (Holding[] memory);
    function getTradeHistory() external view returns (Trade[] memory);
}

contracts/MegaStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./BaseStrategy.sol";
import "./interface/IStrategy.sol";
// TODO: Replace with actual IERC20 interface
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// TODO: Import necessary Hypeliquid interfaces
import "./interface/IHyperliquidRouter.sol";

/**
 * @title MegaStrategy
 * @dev Implements a 3x leveraged long strategy on major assets (wBTC, wETH, wSOL).
 * Inherits from BaseStrategy and implements the IStrategy interface.
 * Meant to be used as the implementation contract for StrategyProxy via delegatecall.
 */
contract MegaStrategy is BaseStrategy {
    // --- Constants ---
    // TODO: Replace with actual mainnet/testnet addresses
    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2; // Example: Mainnet WETH
    address public constant WBTC = 0x2260FAC5E5542a773Aa44fBCfeDf7C193bc2C599; // Example: Mainnet WBTC
    address public constant WSOL = 0xD31a59c85aE9D8edEFeC411D448f90841571b89c;
    address public constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48; // Example: Mainnet USDC

    address[] public strategyAssets; // Assets managed by this strategy

    // Portfolio index mappings
    mapping(address => uint16) private assetToIndex;

    // --- State Variables (Managed Per Proxy via DelegateCall) ---

    // Stores the owner of the proxy contract that this instance serves
    address public proxyOwner;

    // User's position data (keyed by proxy owner address stored in proxyOwner variable)
    struct PositionData {
        uint256 collateralDeposited; // Total USDC collateral deposited via proxy
        uint256 entryValueUsd; // Initial value of the position in USD at entry/last rebalance
        mapping(address => uint256) assetAmounts; // Amount of each strategy asset held
        bool initialized; // Flag to ensure initialize is called only once
    }
    // Note: Since this contract's state lives in the proxy's storage,
    // these mappings effectively store data per proxy instance.
    PositionData internal position; // Stores data for the single proxy owner context

    // Trade history for the user (proxy owner)
    Trade[] internal tradeHistory;

    // --- Events ---
    event Initialized(address indexed owner);
    event PositionUpdated(address indexed owner, string action, uint256 amount);
    event Rebalanced(address indexed owner);
    event TradeRecorded(address indexed owner, Trade trade);

    // Events for liquidity changes
    event LiquidityAdded(address indexed owner, uint256 amount);
    event LiquidityRemoved(address indexed owner, uint256 amount);

    // --- Constructor ---
    /**
     * @dev Sets up the base strategy parameters.
     * @param _hypeliquidRouter Address of the Hypeliquid DEX router.
     * @param _platformFeeBps Platform fee in basis points.
     */
    constructor(address _hypeliquidRouter, uint256 _platformFeeBps)
        BaseStrategy(_hypeliquidRouter, _platformFeeBps, 3) // MegaStrategy uses 3x leverage
    {
        strategyAssets = [WBTC, WETH, WSOL];
        
        // Initialize index mappings (these should match Hyperliquid's indices)
        assetToIndex[WBTC] = 0;  // BTC index
        assetToIndex[WETH] = 1;  // ETH index
        assetToIndex[WSOL] = 2;  // SOL index
    }

    // --- Initialization ---
    /**
     * @dev Initializes the strategy implementation for a specific proxy owner.
     * Should be called once by the proxy owner through the proxy via delegatecall.
     * @param _owner The address of the StrategyProxy owner.
     */
    function initialize(address _owner) external {
        // Remove redundant check that conflicts with position.initialized
        // require(proxyOwner == address(0), "Already initialized");
        // Prevent re-initialization within the same proxy's storage context
        require(!position.initialized, "MegaStrategy: Already initialized");
        require(_owner != address(0), "MegaStrategy: Owner cannot be zero");

        proxyOwner = _owner;
        position.initialized = true;
        emit Initialized(_owner);
    }

    // --- IStrategy Interface Implementation ---

    /**
     * @dev Increases the user's position by adding collateral and buying assets.
     * Assumes equal weighting for simplicity.
     */
    function addLiquidity(uint256 amount, address feeRecipient) external override {
        // No need for msg.sender check in delegatecall context
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");

        uint256 fee = _calculateFee(amount);
        uint256 amountAfterFee = amount - fee;

        position.collateralDeposited += amountAfterFee;

        // Calculate how much to buy of each asset
        uint256 numAssets = strategyAssets.length;
        uint256 valuePerAsset = (amountAfterFee * uint256(leverage)) / numAssets;

        for(uint i = 0; i < numAssets; i++) {
            address asset = strategyAssets[i];
            uint16 assetIndex = assetToIndex[asset];
            uint256 assetPrice = IHyperliquidRouter(hypeliquidRouter).getPrice(assetIndex);
            require(assetPrice > 0, "MegaStrategy: Invalid asset price");
            uint256 amountToBuy = (valuePerAsset * 1e18) / assetPrice;
            
            position.assetAmounts[asset] += amountToBuy;
        }

        position.entryValueUsd += amountAfterFee * uint256(leverage);

        Trade memory newTrade = Trade({
            timestamp: block.timestamp,
            tradeType: "Add Liquidity",
            assetIn: USDC,
            amountIn: amount,
            assetOut: address(0),
            amountOut: 0,
            feeAmount: fee,
            feeAsset: USDC
        });
        tradeHistory.push(newTrade);
        emit TradeRecorded(proxyOwner, newTrade);

        if (fee > 0 && feeRecipient != address(0)) {
            IERC20(USDC).transfer(feeRecipient, fee);
        }

        emit LiquidityAdded(proxyOwner, amount);
        emit PositionUpdated(proxyOwner, "Add Liquidity", amount);
    }

    /**
     * @dev Decreases the user's position by selling assets and reducing collateral tracking.
     */
    function removeLiquidity(uint256 amount, address feeRecipient) external override {
        // No need for msg.sender check in delegatecall context
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");
        require(amount <= position.collateralDeposited, "MegaStrategy: Insufficient liquidity");

        uint256 fee = _calculateFee(amount);
        uint256 amountBeforeFee = amount;
        uint256 amountAfterFee = amount - fee;

        // Calculate proportional reduction for each asset
        uint256 numAssets = strategyAssets.length;
        for(uint i = 0; i < numAssets; i++) {
            address asset = strategyAssets[i];
            if (position.collateralDeposited > 0) {
                uint256 reduction = (position.assetAmounts[asset] * amount) / position.collateralDeposited;
                position.assetAmounts[asset] -= reduction;
            }
        }

        position.collateralDeposited -= amountBeforeFee;
        position.entryValueUsd -= amountBeforeFee * uint256(leverage);

        Trade memory newTrade = Trade({
            timestamp: block.timestamp,
            tradeType: "Remove Liquidity",
            assetIn: address(0),
            amountIn: 0,
            assetOut: USDC,
            amountOut: amountBeforeFee,
            feeAmount: fee,
            feeAsset: USDC
        });
        tradeHistory.push(newTrade);
        emit TradeRecorded(proxyOwner, newTrade);

        if (fee > 0 && feeRecipient != address(0)) {
            IERC20(USDC).transfer(feeRecipient, fee);
        }

        emit LiquidityRemoved(proxyOwner, amount);
        emit PositionUpdated(proxyOwner, "Remove Liquidity", amount);
    }

    /**
     * @dev Executes rebalancing logic to maintain equal weights and leverage.
     */
    function execute() external override {
        // No need for msg.sender check in delegatecall context
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");

        // Get total portfolio value and weights
        uint256[] memory currentValues = new uint256[](strategyAssets.length);
        uint256 totalValue = 0;

        for(uint i = 0; i < strategyAssets.length; i++) {
            address asset = strategyAssets[i];
            uint16 assetIndex = assetToIndex[asset];
            uint256 price = IHyperliquidRouter(hypeliquidRouter).getPrice(assetIndex);
            require(price > 0, "MegaStrategy: Invalid asset price for rebalance");
            currentValues[i] = position.assetAmounts[asset] * price / 1e18;
            totalValue += currentValues[i];
        }

        if (totalValue == 0) return;

        // Target weight per asset (equal weight)
        uint256 targetValue = totalValue / strategyAssets.length;
        uint256 threshold = targetValue * 5 / 100; // 5% threshold

        // Rebalance if needed
        for(uint i = 0; i < strategyAssets.length; i++) {
            address asset = strategyAssets[i];
            uint256 deviation = absDiff(currentValues[i], targetValue);
            
            if (deviation > threshold) {
                if (currentValues[i] > targetValue) {
                    // Sell excess
                    uint256 excessValue = currentValues[i] - targetValue;
                    uint16 assetIndex = assetToIndex[asset];
                    uint256 price = IHyperliquidRouter(hypeliquidRouter).getPrice(assetIndex);
                    require(price > 0, "MegaStrategy: Invalid asset price for sell rebalance");
                    uint256 amountToSell = (excessValue * 1e18) / price;
                    
                    IHyperliquidRouter(hypeliquidRouter).swap(asset, USDC, amountToSell);
                    position.assetAmounts[asset] -= amountToSell;
                } else {
                    // Buy more
                    uint256 shortfall = targetValue - currentValues[i];
                    uint16 assetIndex = assetToIndex[asset];
                    uint256 price = IHyperliquidRouter(hypeliquidRouter).getPrice(assetIndex);
                    require(price > 0, "MegaStrategy: Invalid asset price for buy rebalance");
                    uint256 amountToBuy = (shortfall * 1e18) / price;
                    
                    IHyperliquidRouter(hypeliquidRouter).swap(USDC, asset, amountToBuy);
                    position.assetAmounts[asset] += amountToBuy;
                }
            }
        }

        emit Rebalanced(proxyOwner);
    }

    // Helper function for absolute difference
    function absDiff(uint256 a, uint256 b) internal pure returns (uint256) {
        return a > b ? a - b : b - a;
    }

    /**
     * @dev Calculates current Profit and Loss.
     */
    function getProfitLoss() external view override returns (int256 pnl) {
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");

        // TODO: Implement PnL Calculation
        // 1. Get current market value of all assets in position.assetAmounts using price feeds.
        // 2. Calculate total current value.
        // 3. PnL = Total Current Value - position.collateralDeposited (or more sophisticated calculation based on entry values).
        // Return mock value for now
        return 0;
    }

     /**
     * @dev Returns the owner address associated with this strategy instance (via the proxy).
     */
    function getOwner() external view override returns (address ownerAddress) { // Added override
        // Returns the owner set during initialization for this proxy's context
        return proxyOwner;
    }

    /**
     * @dev Returns the current balance of the strategy.
     */
    function getBalance() external view override returns (uint256) {
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");
        return position.collateralDeposited;
    }

    /**
     * @dev Returns the current holdings of all assets.
     */
    function getHoldings() external view override returns (Holding[] memory) {
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");
        Holding[] memory holdings = new Holding[](strategyAssets.length);
        for(uint i = 0; i < strategyAssets.length; i++) {
            holdings[i] = Holding({
                asset: strategyAssets[i],
                amount: position.assetAmounts[strategyAssets[i]],
                valueUsd: 0 // Optionally implement value calculation
            });
        }
        return holdings;
    }

    /**
     * @dev Returns the trade history.
     */
    function getTradeHistory() external view override returns (Trade[] memory) {
        require(proxyOwner != address(0), "MegaStrategy: Not initialized");
        return tradeHistory;
    }

    // --- Internal Helper Functions ---
    // TODO: Add helpers for DEX interaction, price fetching, etc.

}

